mapper.select:
select a.id,a.name,a.category_id,a.price,a.image, a.description,a.status,a.update_time,b.name as categoryname, c.name as dishflavor_name,c.id as dishflavor_id, c.dish_id as dishflavor_dishid,c.value as dishflavor_value from dish a join category b on a.category_id=b.id left join dish_flavor c on a.id=c.dish_id
a.name like '%${name}%'
a.category_id=#{categoryId}
a.status=#{status}
order by a.update_time desc

mapper.update:
update dish
set status = ${status}
where id=#{id}

mapper.select:
select count(id) from dish


        and status = #{status}


        and category_id = #{categoryId}
